import React, { useEffect, useState } from "react";
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css'

const StateData = () => {

    const [data, setdata] = useState([]);
    const getCovidData = async () => {
        const res = await fetch('https://api.covid19india.org/data.json');
        const orgData = await res.json();
        console.log(orgData.statewise);
        setdata(orgData.statewise);
    }


    useEffect(() => {
        getCovidData();
    }, []);

    return (
        <>
            <div className="container-fluid mt-5">
                <div className="main-heading">
                    <h1 className="mb-5 text-center"> <span className=" text-danger font-weight-bold"> INDIA </span> COVID-19 Dashboard</h1>
                </div>
                <div className="table table-responsive ">
                    <table className="table table-hover table-striped ">
                        <thead className="table-dark">
                            <tr>
                                <th>State</th>
                                <th>Confirmed</th>
                                <th>Recovered</th>
                                <th>Deaths</th>
                                <th>Active</th>
                                <th>Update</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                data.map((val, index) => {
                                    return (
                                        <tr>
                                            <th>{val.state}</th>
                                            <td>{val.confirmed}</td>
                                            <td>{val.recovered}</td>
                                            <td>{val.deaths}</td>
                                            <td>{val.active}</td>
                                            <td>{val.lastupdatedtime}</td>
                                        </tr>
                                    )
                                    active: "861404"
confirmed: "29627763"
deaths: "379551"
deltaconfirmed: "57678"
deltadeaths: "2489"
deltarecovered: "102220"
lastupdatedtime: "15/06/2021 21:39:29"
migratedother: "11664"
recovered: "28375144"
state: "Total"
statecode: "TT"
statenotes: ""
                                })
                            }
                        </tbody>

                    </table>
                </div>
            </div>
        </>
    )
}

export default StateData;
