import React, { useState } from "react";
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css'
import '../component/statedata.css'
import Header from '../header'

const StateData = () => {

    const [data, setdata] = useState([]);
    const getCovidData = async () => {
        // const res = 
        await fetch('https://data.covid19india.org/v4/min/data.min.json')
            .then(response => response.json())    // one extra step
            .then(data => {
                // console.log(data)
                const orgData = data;
                setdata(orgData);
            })
            .catch(error => console.error(error));
        console.log("Ans");
        console.log(orgData);

    }
    //     // https://api.covid19india.org/data.json
    // https://data.covid19india.org/v4/min/data.min.json

    getCovidData();
    // useEffect(() => {
    // }, []);

    return (
        <>
            <div className=" mt-5">
                <Header />
                <div className="table table-responsive container-fluid ">
                    <table className="table table-hover table-striped table-fluid">
                        <thead className="table-dark">
                            <tr>
                                <th className="headerdd">State</th>
                                <th className="headerdd">Confirmed</th>
                                <th className="headerdd">Recovered</th>
                                <th className="headerdd">Deaths</th>
                                <th className="headerdd">Active</th>
                                <th className="headerdd">Update</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                data.map((val, index) => {
                                    return (
                                        <tr key={index}>
                                            <th>{val.state}</th>
                                            <td>{val.confirmed}</td>
                                            <td>{val.recovered}</td>
                                            <td>{val.deaths}</td>
                                            <td>{val.active}</td>
                                            <td>{val.lastupdatedtime}</td>
                                        </tr>
                                    )

                                })
                            }
                        </tbody>

                    </table>
                </div>
            </div>
        </>
    )
}

export default StateData;
